mapper.select:
SELECT house_info.* FROM house_info
LEFT JOIN house_owner ON house_info.owner_id=house_owner.owner_id
WHERE house_owner.owner_name=#{ownerName}

mapper.update:
UPDATE house_info,house_type

	house_number=#{houseNumber},
	house_area=#{houseArea},
	house_in_time=#{houseInTime},
	house_info.house_type_id=#{houseType.houseTypeId}

where house_id=#{houseId}

mapper.select:
SELECT house_id FROM house_info i
LEFT JOIN building_info b ON b.build_id=i.build_id
WHERE build_number=#{buildNumber} AND house_unit=#{houseUnit} AND house_number=#{houseNumber}
